sp_widget.client_script:
api.controller = function(cabrillo, $rootScope, $scope) {
    /* widget controller */
    var c = this;
    c.mealPlanRecordID = "";
    c.dayRecordID = "";
    c.add = function($event) {
        //prevent clicking the parent widget card
        $event.preventDefault();
        if ($event.stopPropagation) {
            $event.stopPropagation();
        }

        c.server.get({
            "action": "addRecipe",
            "mealPlanRecordID": c.mealPlanRecordID,
            "dayRecordID": c.dayRecordID,
            "recipeRecordID": c.data.sys_id,

        }).then(function(resp) {
            c.broadcastRecipeSelectionModified();
        });
    };

    c.remove = function($event) {
        //prevent clicking the parent widget card
        $event.preventDefault();
        if ($event.stopPropagation) {
            $event.stopPropagation();
        }

        c.server.get({
            "action": "removeRecipe",
            "mealPlanRecordID": c.mealPlanRecordID,
            "dayRecordID": c.dayRecordID,
            "recipeRecordID": c.data.sys_id,

        }).then(function(resp) {
            c.broadcastRecipeSelectionModified();
        });
    };

    c.broadcastRecipeSelectionModified = function() {
        $rootScope.$broadcast('recipe-modified', {});
    };


    $scope.$on('selectedMealPlanRecord', function($event, mealPlanRecord) {
        c.mealPlanRecordID = mealPlanRecord.recordId;
    });

    $scope.$on('selectedDayRecord', function($event, dayRecord) {
        c.dayRecordID = dayRecord.recordId;
    });

};

sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */
    data.showAddAction = true;

    var isInitialRenderCall = !input || !input.hasOwnProperty('action');
    // runs when the widget is first loaded
    if (isInitialRenderCall) {
        //if the widget is called from server side of the parent widget, widget options are passed into 'options' object
        //if the widget is called from client side of the parent widget, widget options are passed into 'input' object
        var dataFromParentWidget = input || options;
        data.table = dataFromParentWidget.table;
        data.sys_id = dataFromParentWidget.sys_id;
        data.showAddAction = dataFromParentWidget.showAddAction;
        data.showRemoveAction = dataFromParentWidget.showRemoveAction;
    }


    // runs on 
    if (!isInitialRenderCall && input.action == 'addRecipe') {
        var recipeAllocationGR = new GlideRecord("x_62500_mealplan_recipe_allocation");
        recipeAllocationGR.initialize();
        recipeAllocationGR.meal_plan = input.mealPlanRecordID;
        recipeAllocationGR.day = input.dayRecordID;
        recipeAllocationGR.recipe = input.recipeRecordID;
        recipeAllocationGR.insert();
    }

    // runs on 
    if (!isInitialRenderCall && input.action == 'removeRecipe') {
        var recipeAllocationGR = new GlideRecord("x_62500_mealplan_recipe_allocation");
        recipeAllocationGR.addQuery("meal_plan", input.mealPlanRecordID);
        recipeAllocationGR.addQuery("day", input.dayRecordID);
        recipeAllocationGR.addQuery("recipe", input.recipeRecordID);
        recipeAllocationGR.query();
        console.log(recipeAllocationGR.getEncodedQuery());
        if (recipeAllocationGR.next()) {
            recipeAllocationGR.deleteRecord();
        }
    }

})();
